import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { FaWater } from 'react-icons/fa';

import { puneLocations, puneCenter, getRiverColor, riverPaths, preSampledRiver } from './locations';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function App() {
  const [route, setRoute] = useState('home'); // 'home' | 'predict' | 'interpolate'
  const [predictions, setPredictions] = useState({});
  const [selected, setSelected] = useState(null);
  const [interpPoints, setInterpPoints] = useState([]); // points returned from interpolation
  const [sampleCount, setSampleCount] = useState(5);
  const [selectMode, setSelectMode] = useState(null); // 'start' | 'end' | null
  const [startPoint, setStartPoint] = useState(null);
  const [endPoint, setEndPoint] = useState(null);
  
  const [mapInst, setMapInst] = useState(null);
  const [selectedDate, setSelectedDate] = useState(() => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`; // local YYYY-MM-DD
  });


  useEffect(() => {
    // fetch predictions for selectedDate (server needs month & year)
    const [y, m] = selectedDate.slice(0, 7).split('-').map(Number); // YYYY-MM-DD -> [YYYY, MM]
    const fetchAll = async () => {
      try {
        const res = await fetch(`${API_BASE}/predict_all?month=${m}&year=${y}`);
        const body = await res.json();
        const newPred = {};
        for (const item of body.predictions) {
          // match by location name to puneLocations id
          const loc = puneLocations.find(p => p.name === item.location);
          if (loc) newPred[loc.id] = {
            'pH': item['pH'],
            'DO (mg/L)': item['DO (mg/L)'],
            'BOD (mg/L)': item['BOD (mg/L)'],
            'FC MPN/100ml': item['FC MPN/100ml'],
            'TC MPN/100ml': item['TC MPN/100ml'],
            'Water Quality': item['Water Quality']
          };
        }
        // for locations not returned, set null
        for (const loc of puneLocations) if (!newPred[loc.id]) newPred[loc.id] = null;
        setPredictions(newPred);
        // update selected prediction if needed
        if (selected) {
          const sel = puneLocations.find(p => p.id === selected.id);
          setSelected(sel ? { ...sel, prediction: newPred[sel.id] } : null);
        }
      } catch (e) {
        // fallback: clear predictions
        const empty = {};
        for (const loc of puneLocations) empty[loc.id] = null;
        setPredictions(empty);
      }
    };
    fetchAll();
  }, [selectedDate]);

  // update map cursor when entering pick mode
  useEffect(() => {
    if (!mapInst) return;
    const el = mapInst.getContainer();
    if (selectMode) {
      el.style.cursor = 'crosshair';
    } else {
      el.style.cursor = '';
    }
  }, [selectMode, mapInst]);

  // when switching to predict page, clear any pick mode and interpolation points
  useEffect(() => {
    if (route === 'predict') {
      setSelectMode(null);
      setInterpPoints([]);
    }
  }, [route]);

  // re-run interpolation when the selected date changes (if start/end are set)
  useEffect(() => {
    if (route === 'interpolate' && startPoint && endPoint) {
      performInterpolation(startPoint, endPoint);
    }
  }, [selectedDate]);

  const handleMarkerClick = (loc) => {
    setSelected({ ...loc, prediction: predictions[loc.id] });
  };

  const handleMapClick = (e) => {
    console.log('map clicked', selectMode, e && e.latlng && e.latlng.lat);
    if (!selectMode) return;
    const { lat, lng } = e.latlng;
    if (selectMode === 'start') {
      setStartPoint({ latitude: lat, longitude: lng });
      setSelectMode(null);
    } else if (selectMode === 'end') {
      const newEnd = { latitude: lat, longitude: lng };
      setEndPoint(newEnd);
      setSelectMode(null);
      if (route === 'interpolate' && startPoint) {
        // auto-run interpolation when both points are set (only on interpolate page)
        performInterpolation(startPoint, newEnd);
      }
  }
  };

  // react-leaflet hook wrapper to ensure clicks are captured reliably
  const MapClickHandler = ({ onMapClick }) => {
    useMapEvents({
      click: (e) => onMapClick(e)
    });
    return null;
  };

  const toLatLngs = (path) => path.map(p => [p.latitude, p.longitude]);

  // perform interpolation between two picked points by slicing the preSampledRiver
  const performInterpolation = async (startPt, endPt) => {
    if (!startPt || !endPt) return;
    // find nearest indices in the pre-sampled river array and send that slice to backend
    const nearestIndex = (pt) => {
      let best = 0;
      let bestD = Infinity;
      for (let i = 0; i < preSampledRiver.length; i++) {
        const dlat = preSampledRiver[i].latitude - pt.latitude;
        const dlng = preSampledRiver[i].longitude - pt.longitude;
        const d2 = dlat * dlat + dlng * dlng;
        if (d2 < bestD) { bestD = d2; best = i; }
      }
      return best;
    };
    const si = nearestIndex(startPt);
    const ei = nearestIndex(endPt);
    const a = Math.min(si, ei);
    const b = Math.max(si, ei);
    const sub = preSampledRiver.slice(a, b + 1);

    const d = new Date(selectedDate);
    const body = { locations: sub, points: sampleCount, month: d.getMonth()+1, year: d.getFullYear(), pick_from_input: true };
    try {
      const res = await fetch(`${API_BASE}/interpolate_predict`, { method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify(body) });
      const jb = await res.json();
      if (jb.error) { alert('Server: ' + jb.error); return; }
      setInterpPoints(jb.predictions || []);
    } catch (err) { alert('Request failed'); }
  };

  // Inline calendar component
  const Calendar = ({ date, onChange }) => {
    // helpers to ensure local-date handling (avoid UTC shifts from toISOString)
    const parseLocal = (s) => new Date(s + 'T00:00:00');
    const formatLocal = (d) => `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`;

    const [viewDate, setViewDate] = useState(() => {
      const d = parseLocal(date);
      return new Date(d.getFullYear(), d.getMonth(), 1);
    });

    useEffect(() => {
      const d = parseLocal(date);
      setViewDate(new Date(d.getFullYear(), d.getMonth(), 1));
    }, [date]);

    const startOfMonth = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1);
    const endOfMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0);
    const daysInMonth = endOfMonth.getDate();
    const startWeekday = (startOfMonth.getDay() + 6) % 7; // make Monday=0

    const prevMonth = () => setViewDate(new Date(viewDate.getFullYear(), viewDate.getMonth() - 1, 1));
    const nextMonth = () => setViewDate(new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 1));

    const rows = [];
    let cells = [];
    // fill blanks
    for (let i = 0; i < startWeekday; i++) cells.push(null);
    for (let d = 1; d <= daysInMonth; d++) {
      cells.push(new Date(viewDate.getFullYear(), viewDate.getMonth(), d));
    }
    while (cells.length % 7 !== 0) cells.push(null);
    for (let i = 0; i < cells.length; i += 7) rows.push(cells.slice(i, i + 7));

  const selectedObj = parseLocal(date);
  const isSameDay = (a, b) => a && b && a.getFullYear() === b.getFullYear() && a.getMonth() === b.getMonth() && a.getDate() === b.getDate();
  const today = new Date();

    // month and year selectors for faster navigation
    const monthNames = ["January","February","March","April","May","June","July","August","September","October","November","December"];
    const currentYear = new Date().getFullYear();
    const startYear = currentYear - 5;
    const endYear = currentYear + 5;

    return (
      <div className="inline-calendar">
        <div className="cal-head">
          <select aria-label="Month" value={viewDate.getMonth()} onChange={e => setViewDate(new Date(viewDate.getFullYear(), Number(e.target.value), 1))}>
            {monthNames.map((m, i) => <option key={i} value={i}>{m}</option>)}
          </select>
          <select aria-label="Year" value={viewDate.getFullYear()} onChange={e => setViewDate(new Date(Number(e.target.value), viewDate.getMonth(), 1))}>
            {Array.from({length: endYear - startYear + 1}, (_, i) => startYear + i).map(y => <option key={y} value={y}>{y}</option>)}
          </select>
        </div>

        <div className="cal-grid">
          <div className="cal-weekday">Mo</div>
          <div className="cal-weekday">Tu</div>
          <div className="cal-weekday">We</div>
          <div className="cal-weekday">Th</div>
          <div className="cal-weekday">Fr</div>
          <div className="cal-weekday">Sa</div>
          <div className="cal-weekday">Su</div>

          {rows.map((row, ri) => (
            <React.Fragment key={ri}>
              {row.map((d, ci) => {
                const cls = d ? (isSameDay(d, selectedObj) ? ' selected' : (isSameDay(d, today) ? ' today' : '')) : ' empty';
                return (
                  <div key={`${ri}-${ci}`} className={`cal-day${cls}`}>
                    {d ? (
                        <button onClick={() => onChange(formatLocal(d))} aria-label={`Select ${d.toDateString()}`}>
                          {d.getDate()}
                        </button>
                      ) : (
                      <div aria-hidden className="empty-cell" />
                    )}
                  </div>
                );
              })}
            </React.Fragment>
          ))}
        </div>
          <div className="cal-footer">
            <button onClick={() => onChange(formatLocal(new Date()))}>Today</button>
          </div>
      </div>
    );
  };

  // Parameter status helper (returns label and CSS class)
  const getParameterStatus = (param, value) => {
    if (value === null || value === undefined || value === '') return { label: 'N/A', cls: 'status-na' };
    const v = Number(value);
    switch (param) {
      case 'pH':
        if (v >= 6.5 && v <= 8.5) return { label: 'Good', cls: 'status-good' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'DO (mg/L)':
        if (v >= 5.0) return { label: 'Good', cls: 'status-good' };
        if (v >= 3.0) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'BOD (mg/L)':
        if (v <= 3.0) return { label: 'Good', cls: 'status-good' };
        if (v <= 6.0) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'FC MPN/100ml':
      case 'TC MPN/100ml':
        if (v < 500) return { label: 'Good', cls: 'status-good' };
        if (v <= 1000) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'Water Quality':
  // normalize and check for explicit 'non' first so "Non Complying" doesn't match 'comp'
  const _s = String(value).toLowerCase();
  if (_s.includes('non')) return { label: 'Non Complying', cls: 'status-poor' };
  if (_s.includes('comp')) return { label: 'Complying', cls: 'status-good' };
  return { label: String(value), cls: 'status-na' };
      default:
        return { label: '', cls: '' };
    }
  };

  return (
  <div className="page">
      {route !== 'home' && (
        <aside className="sidebar">
          <div className="brand">
            <FaWater size={28} color="#fff" />
            <div>
              <h1>RiverWatch</h1>
              <p>Water quality predictions for Pune rivers</p>
            </div>
          </div>
  <div className="legend">
            <div><span className="dot blue"/> Mula</div>
            <div><span className="dot teal"/> Mutha</div>
            <div><span className="dot orange"/> Mula-Mutha</div>
          </div>

          <div className="sidebar-info">
            <h3>Rivers</h3>
            <ul className="river-list">
              <li><span className="dot blue"/> <strong>Mula</strong>
                <div className="river-desc">Originates to the north â€” urban stretches through western Pune with monitoring points along bridges.</div>
              </li>
              <li><span className="dot teal"/> <strong>Mutha</strong>
                <div className="river-desc">Flows from Khadakvasla dam through Pune central areas; many monitoring points track water health downstream.</div>
              </li>
              <li><span className="dot orange"/> <strong>Mula-Mutha</strong>
                <div className="river-desc">Confluence section where Mula and Mutha meet â€” sensitive zone for mixing and quality changes.</div>
              </li>
            </ul>

            <h3 style={{marginTop:12}}>Parameters</h3>
            <ul className="param-names">
              <li>pH</li>
              <li>DO (mg/L)</li>
              <li>BOD (mg/L)</li>
              <li>FC MPN/100ml</li>
              <li>TC MPN/100ml</li>
              <li>Water Quality (Complying / Non Complying)</li>
            </ul>
          </div>
        </aside>
      )}

      <main className="main">
        {route === 'home' ? (
          <div className="home-page">
            <div className="home-card">
              <div className="home-hero">
                <div className="logo"><FaWater size={28} color="#fff" /></div>
                <div>
                  <h1>RiverWatch â€” Pune Rivers</h1>
                  <p className="muted">Water quality predictions for Pune rivers</p>
                </div>
              </div>
              <p>Explore predicted water quality across the Mula, Mutha and Mula-Mutha rivers. Compare parameters: pH, DO, BOD, FC and TC, and view compliance status for a selected date.</p>

              <div className="columns">
                <div className="col rivers">
                  <h3><span className="icon">ðŸŒŠ</span> Rivers</h3>
                  <div className="river-item">
                    <strong>Mula</strong>
                    <div className="desc">Urban stretches in western Pune with multiple monitoring points.</div>
                    <div className="desc" style={{marginTop:6}}>Frequently impacted by urban runoff and untreated discharges; used locally for irrigation and limited recreation.</div>
                  </div>
                  <div className="river-item">
                    <strong>Mutha</strong>
                    <div className="desc">Flows from Khadakvasla dam through central Pune; many monitoring sites downstream.</div>
                    <div className="desc" style={{marginTop:6}}>Supplies water to downstream areas; sections receive municipal and industrial inputs that affect DO and bacterial counts.</div>
                  </div>
                  <div className="river-item">
                    <strong>Mula-Mutha</strong>
                    <div className="desc">Confluence zone sensitive to mixing and water quality changes.</div>
                    <div className="desc" style={{marginTop:6}}>A dynamic stretch where quality can change rapidly with flow and season; supports localized biodiversity but is vulnerable to pollution pulses.</div>
                  </div>
                </div>

                <div className="col center">
                  <h3><span className="icon">ðŸ’§</span> About</h3>
                  <p style={{lineHeight:1.6, color:'#334155'}}>This site reproduces the water-quality model from the mobile app and exposes interpolation along river polylines.</p>
                  <p style={{lineHeight:1.6, color:'#334155'}}>Pick two points on the map to sample locations between them. Use Predict for single-point queries.</p>
                </div>

                <div className="col params">
                  <h3><span className="icon">ðŸ§ª</span> Parameters</h3>
                  <ul className="param-names">
                    <li>
                      <strong>pH</strong>
                      <div className="desc">Measures water acidity/alkalinity; values outside ~6.5â€“8.5 can stress aquatic life and alter chemical solubility.</div>
                    </li>
                    <li>
                      <strong>DO</strong>
                      <div className="desc">Dissolved oxygen available to organisms; low DO often signals pollution or eutrophication and threatens fish and invertebrates.</div>
                    </li>
                    <li>
                      <strong>BOD</strong>
                      <div className="desc">Biological Oxygen Demand â€” oxygen used by microbes to decompose organic matter; higher BOD indicates heavier organic pollution.</div>
                    </li>
                    <li>
                      <strong>FC / TC</strong>
                      <div className="desc">Faecal (FC) and Total (TC) coliform counts indicate bacterial contamination; elevated levels imply fecal pollution and health risk.</div>
                    </li>
                  </ul>
                </div>
              </div>

              <div className="home-cta" style={{marginTop:18}}>
                <button className="home-cta" onClick={() => setRoute('predict')}>Go to Predict</button>
                <button className="home-cta" style={{marginLeft:10}} onClick={() => setRoute('interpolate')}>Go to Interpolate</button>
              </div>
            </div>
          </div>
        ) : (
          <div className="main-content">
            <MapContainer center={[puneCenter.latitude, puneCenter.longitude]} zoom={puneCenter.zoom} className="map" whenCreated={m => setMapInst(m)}>
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              <MapClickHandler onMapClick={handleMapClick} />

              {puneLocations.map(loc => (
                <CircleMarker key={loc.id}
                  center={[loc.coordinate.latitude, loc.coordinate.longitude]}
                  pathOptions={{ color: getRiverColor(loc.river), fillColor: getRiverColor(loc.river), fillOpacity: 0.9 }}
                  radius={10}
                  eventHandlers={{ click: () => handleMarkerClick(loc) }}
                >
                  <Popup>
                    <div className="popup-card">
                      <div className="popup-header">
                        <strong className="popup-title">{loc.name}</strong>
                        <div className="muted popup-sub">{loc.river}</div>
                      </div>

                      {predictions[loc.id] ? (
                        <ul className="param-list popup-grid">
                          {Object.entries(predictions[loc.id]).map(([k, v]) => {
                            const status = getParameterStatus(k, v);
                            if (k === 'Water Quality') {
                              return (
                                <li className="param-row" key={k}>
                                  <span className="param-name">{k}</span>
                                  <span className="param-value"><span className={`badge ${status.cls}`}>{status.label}</span></span>
                                </li>
                              );
                            }

                            return (
                              <li className="param-row" key={k}>
                                <span className="param-name">{k}</span>
                                <span className="param-value"><span className="val-text">{v}</span><span className={`badge ${status.cls}`}>{status.label}</span></span>
                              </li>
                            );
                          })}
                        </ul>
                      ) : (
                        <div>Loading predictions...</div>
                      )}
                    </div>
                  </Popup>
                </CircleMarker>
              ))}

              {/* show start/end & interpolated points only on interpolate page */}
              {route === 'interpolate' && startPoint ? <Marker position={[startPoint.latitude, startPoint.longitude]}><Popup>Start</Popup></Marker> : null}
              {route === 'interpolate' && endPoint ? <Marker position={[endPoint.latitude, endPoint.longitude]}><Popup>End</Popup></Marker> : null}

              {route === 'interpolate' && interpPoints.map((pt, idx) => (
                <CircleMarker key={`ip-${idx}`} center={[pt.latitude, pt.longitude]} radius={6} pathOptions={{ color: '#000', fillColor: '#00BCD4', fillOpacity: 0.9 }}>
                  <Popup>
                    <div style={{minWidth:200}}>
                      <div><strong>Interpolated point</strong></div>
                      <div className="muted">Nearest: {pt.nearest_location || 'â€”'}</div>
                      <ul className="param-list popup-grid">
                        <li className="param-row"><span className="param-name">pH</span><span className="param-value">{pt['pH']}</span></li>
                        <li className="param-row"><span className="param-name">DO (mg/L)</span><span className="param-value">{pt['DO (mg/L)']}</span></li>
                        <li className="param-row"><span className="param-name">BOD (mg/L)</span><span className="param-value">{pt['BOD (mg/L)']}</span></li>
                        <li className="param-row"><span className="param-name">FC</span><span className="param-value">{pt['FC MPN/100ml']}</span></li>
                        <li className="param-row"><span className="param-name">TC</span><span className="param-value">{pt['TC MPN/100ml']}</span></li>
                        <li className="param-row"><span className="param-name">Quality</span><span className="param-value">{pt['Water Quality']}</span></li>
                      </ul>
                    </div>
                  </Popup>
                </CircleMarker>
              ))}
            </MapContainer>

            <div className="detail-panel" style={{ '--river-color': getRiverColor(selected ? selected.river : 'Mula') }}>
              <div className="detail-calendar">
                <Calendar date={selectedDate} onChange={d => setSelectedDate(d)} />
              </div>
              <div style={{marginTop:12}}>
                <div className="controls-row">
                  <button onClick={() => setSelectMode(selectMode==='start' ? null : 'start')} className={selectMode==='start' ? 'active' : ''}>Pick Start</button>
                  <button onClick={() => setSelectMode(selectMode==='end' ? null : 'end')} className={selectMode==='end' ? 'active' : ''}>Pick End</button>
                  <button onClick={() => { setSelectMode(null); setInterpPoints([]); }}>Cancel</button>
                  <button onClick={async () => {
                    if (!startPoint || !endPoint) return alert('Pick start and end by clicking the map');
                    // find nearest indices in the pre-sampled river array and send that slice to backend
                    const nearestIndex = (pt) => {
                      let best = 0;
                      let bestD = Infinity;
                      for (let i = 0; i < preSampledRiver.length; i++) {
                        const dlat = preSampledRiver[i].latitude - pt.latitude;
                        const dlng = preSampledRiver[i].longitude - pt.longitude;
                        const d2 = dlat * dlat + dlng * dlng;
                        if (d2 < bestD) { bestD = d2; best = i; }
                      }
                      return best;
                    };
                    const si = nearestIndex(startPoint);
                    const ei = nearestIndex(endPoint);
                    const a = Math.min(si, ei);
                    const b = Math.max(si, ei);
                    const sub = preSampledRiver.slice(a, b + 1);

                    const d = new Date(selectedDate);
                    const body = { locations: sub, points: sampleCount, month: d.getMonth()+1, year: d.getFullYear(), pick_from_input: true };
                    try {
                      const res = await fetch(`${API_BASE}/interpolate_predict`, { method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify(body) });
                      const jb = await res.json();
                      if (jb.error) return alert('Server: ' + jb.error);
                      setInterpPoints(jb.predictions || []);
                    } catch (err) { alert('Request failed'); }
                  }}>Interpolate</button>
                  <div style={{marginTop:8}}>
                    <div style={{marginTop:8}}>Pick start and end on the map and click <strong>Interpolate</strong> (or picking the end will auto-run).</div>
                    <div style={{display:'flex', gap:8, alignItems:'center', marginTop:6}}>
                      <label style={{display:'flex', gap:6, alignItems:'center'}}>Pick k:
                        <input type="number" min={1} max={50} value={sampleCount} onChange={e => setSampleCount(Number(e.target.value))} style={{width:80, marginLeft:6}} />
                      </label>
                    </div>
                  </div>
                </div>
                <div style={{marginTop:8}}>
                  <div>Start: {startPoint ? `${startPoint.latitude.toFixed(5)}, ${startPoint.longitude.toFixed(5)}` : 'â€”'}</div>
                  <div>End: {endPoint ? `${endPoint.latitude.toFixed(5)}, ${endPoint.longitude.toFixed(5)}` : 'â€”'}</div>
                </div>
                <div style={{marginTop:8}}>
                  <strong>Pick mode:</strong> {selectMode || 'none'} {selectMode ? <span style={{marginLeft:8, color:'#777'}}>Click on the map to set point</span> : null}
                </div>
                <div style={{marginTop:8}}>
                  <strong>Pick mode:</strong> {selectMode || 'none'}
                </div>
              </div>
              <div className="selected-date">Selected date: <strong>{new Date(selectedDate).toLocaleDateString()}</strong></div>
              {selected ? (
                <div>
                  <h2>{selected.name}</h2>
                  <p className="muted">{selected.river} â€¢ {selected.description}</p>
                  <h3>Prediction for {new Date(selectedDate).toLocaleDateString()}</h3>
                  {selected.prediction ? (
                    <ul className="param-list large">
                      {Object.entries(selected.prediction).filter(([k]) => k !== 'Water Quality').map(([k, v]) => {
                        const status = getParameterStatus(k, v);
                        return <li key={k}><span className="param">{k}</span><span className="value">{v}</span><span className={`badge ${status.cls}`}>{status.label}</span></li>
                      })}
                      {(() => {
                        const q = selected.prediction['Water Quality'];
                        const status = getParameterStatus('Water Quality', q);
                        return (
                          <li className="quality">
                            <span className="param">Water Quality</span>
                            <span className="param-value"><span className={`badge ${status.cls}`}>{status.label}</span></span>
                          </li>
                        );
                      })()}
                    </ul>
                  ) : <div>Loading...</div>}
                </div>
              ) : (
                <div className="placeholder">Select a location on the map or from the list.</div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
