import React from 'react';

export default function Calendar({ date, onChange }) {
  const base = date ? new Date(date) : new Date();
  const [viewMonth, setViewMonth] = React.useState(base.getMonth());
  const [viewYear, setViewYear] = React.useState(base.getFullYear());

  React.useEffect(() => {
    const d = date ? new Date(date) : new Date();
    setViewMonth(d.getMonth());
    setViewYear(d.getFullYear());
  }, [date]);

  const startOfMonth = (y, m) => new Date(y, m, 1);
  const daysInMonth = (y, m) => new Date(y, m + 1, 0).getDate();

  const weeks = [];
  const firstDay = startOfMonth(viewYear, viewMonth).getDay(); // 0=Sun
  const leading = (firstDay + 6) % 7; // shift to Monday first (0=Mon)
  const total = leading + daysInMonth(viewYear, viewMonth);
  const rows = Math.ceil(total / 7);

  let day = 1 - leading;
  for (let r = 0; r < rows; r++) {
    const week = [];
    for (let c = 0; c < 7; c++, day++) {
      const cellDate = new Date(viewYear, viewMonth, day);
      week.push(cellDate);
    }
    weeks.push(week);
  }

  const monthNames = Array.from({ length: 12 }, (_, i) => new Date(2000, i).toLocaleString(undefined, { month: 'long' }));

  const selectDate = (d) => {
    const iso = `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
    onChange && onChange(iso);
  };

  const selected = date ? new Date(date) : null;

  return (
    <div className="inline-calendar" style={{ padding: 8 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <button className="small" onClick={() => { const m = viewMonth - 1; if (m < 0) { setViewYear(v => v - 1); setViewMonth(11); } else setViewMonth(m); }}>{'◀'}</button>
        <div style={{ display: 'flex', gap: 8 }}>
          <select value={viewMonth} onChange={e => setViewMonth(Number(e.target.value))}>
            {monthNames.map((mn, idx) => <option key={mn} value={idx}>{mn}</option>)}
          </select>
          <select value={viewYear} onChange={e => setViewYear(Number(e.target.value))}>
            {Array.from({ length: 8 }).map((_, i) => viewYear - 4 + i).map(y => <option key={y} value={y}>{y}</option>)}
          </select>
        </div>
        <button className="small" onClick={() => { const m = viewMonth + 1; if (m > 11) { setViewYear(v => v + 1); setViewMonth(0); } else setViewMonth(m); }}>{'▶'}</button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7,1fr)', gap: 4, marginTop: 8, fontSize: 12, fontWeight: 700, textAlign: 'center' }}>
        {['Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa', 'Su'].map(d => <div key={d}>{d}</div>)}
      </div>

      <div style={{ marginTop: 8 }}>
        {weeks.map((week, i) => (
          <div key={i} style={{ display: 'grid', gridTemplateColumns: 'repeat(7,1fr)', gap: 4, marginBottom: 4 }}>
            {week.map(d => {
              const inMonth = d.getMonth() === viewMonth;
              const isSelected = selected && d.getFullYear() === selected.getFullYear() && d.getMonth() === selected.getMonth() && d.getDate() === selected.getDate();
              return (
                <button key={d.toISOString()} onClick={() => inMonth && selectDate(d)} style={{ padding: 8, borderRadius: 6, border: 'none', background: isSelected ? 'var(--primary)' : (inMonth ? '#f8fafc' : 'transparent'), color: isSelected ? '#fff' : (inMonth ? '#0b3b52' : '#94a3b8') }}>{d.getDate()}</button>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}
