import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { FaWater } from 'react-icons/fa';

import { puneLocations, puneCenter, getRiverColor } from './locations';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function App() {
  const [predictions, setPredictions] = useState({});
  const [selected, setSelected] = useState(null);
  const [selectedDate, setSelectedDate] = useState(() => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`; // local YYYY-MM-DD
  });


  useEffect(() => {
    // fetch predictions for selectedDate (server needs month & year)
    const [y, m] = selectedDate.slice(0, 7).split('-').map(Number); // YYYY-MM-DD -> [YYYY, MM]
    const fetchAll = async () => {
      try {
        const res = await fetch(`${API_BASE}/predict_all?month=${m}&year=${y}`);
        const body = await res.json();
        const newPred = {};
        for (const item of body.predictions) {
          // match by location name to puneLocations id
          const loc = puneLocations.find(p => p.name === item.location);
          if (loc) newPred[loc.id] = {
            'pH': item['pH'],
            'DO (mg/L)': item['DO (mg/L)'],
            'BOD (mg/L)': item['BOD (mg/L)'],
            'FC MPN/100ml': item['FC MPN/100ml'],
            'TC MPN/100ml': item['TC MPN/100ml'],
            'Water Quality': item['Water Quality']
          };
        }
        // for locations not returned, set null
        for (const loc of puneLocations) if (!newPred[loc.id]) newPred[loc.id] = null;
        setPredictions(newPred);
        // update selected prediction if needed
        if (selected) {
          const sel = puneLocations.find(p => p.id === selected.id);
          setSelected(sel ? { ...sel, prediction: newPred[sel.id] } : null);
        }
      } catch (e) {
        // fallback: clear predictions
        const empty = {};
        for (const loc of puneLocations) empty[loc.id] = null;
        setPredictions(empty);
      }
    };
    fetchAll();
  }, [selectedDate]);

  const handleMarkerClick = (loc) => {
    setSelected({ ...loc, prediction: predictions[loc.id] });
  };

  const toLatLngs = (path) => path.map(p => [p.latitude, p.longitude]);

  // Inline calendar component
  const Calendar = ({ date, onChange }) => {
    // helpers to ensure local-date handling (avoid UTC shifts from toISOString)
    const parseLocal = (s) => new Date(s + 'T00:00:00');
    const formatLocal = (d) => `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`;

    const [viewDate, setViewDate] = useState(() => {
      const d = parseLocal(date);
      return new Date(d.getFullYear(), d.getMonth(), 1);
    });

    useEffect(() => {
      const d = parseLocal(date);
      setViewDate(new Date(d.getFullYear(), d.getMonth(), 1));
    }, [date]);

    const startOfMonth = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1);
    const endOfMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0);
    const daysInMonth = endOfMonth.getDate();
    const startWeekday = (startOfMonth.getDay() + 6) % 7; // make Monday=0

    const prevMonth = () => setViewDate(new Date(viewDate.getFullYear(), viewDate.getMonth() - 1, 1));
    const nextMonth = () => setViewDate(new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 1));

    const rows = [];
    let cells = [];
    // fill blanks
    for (let i = 0; i < startWeekday; i++) cells.push(null);
    for (let d = 1; d <= daysInMonth; d++) {
      cells.push(new Date(viewDate.getFullYear(), viewDate.getMonth(), d));
    }
    while (cells.length % 7 !== 0) cells.push(null);
    for (let i = 0; i < cells.length; i += 7) rows.push(cells.slice(i, i + 7));

  const selectedObj = parseLocal(date);
  const isSameDay = (a, b) => a && b && a.getFullYear() === b.getFullYear() && a.getMonth() === b.getMonth() && a.getDate() === b.getDate();
  const today = new Date();

    return (
      <div className="inline-calendar">
        <div className="cal-head">
          <button onClick={prevMonth} aria-label="Previous month">‹</button>
          <div className="cal-title">{viewDate.toLocaleString(undefined, { month: 'long' })} {viewDate.getFullYear()}</div>
          <button onClick={nextMonth} aria-label="Next month">›</button>
        </div>

        <div className="cal-grid">
          <div className="cal-weekday">Mo</div>
          <div className="cal-weekday">Tu</div>
          <div className="cal-weekday">We</div>
          <div className="cal-weekday">Th</div>
          <div className="cal-weekday">Fr</div>
          <div className="cal-weekday">Sa</div>
          <div className="cal-weekday">Su</div>

          {rows.map((row, ri) => (
            <React.Fragment key={ri}>
              {row.map((d, ci) => {
                const cls = d ? (isSameDay(d, selectedObj) ? ' selected' : (isSameDay(d, today) ? ' today' : '')) : ' empty';
                return (
                  <div key={`${ri}-${ci}`} className={`cal-day${cls}`}>
                    {d ? (
                        <button onClick={() => onChange(formatLocal(d))} aria-label={`Select ${d.toDateString()}`}>
                          {d.getDate()}
                        </button>
                      ) : (
                      <div aria-hidden className="empty-cell" />
                    )}
                  </div>
                );
              })}
            </React.Fragment>
          ))}
        </div>
          <div className="cal-footer">
            <button onClick={() => onChange(formatLocal(new Date()))}>Today</button>
          </div>
      </div>
    );
  };

  // Parameter status helper (returns label and CSS class)
  const getParameterStatus = (param, value) => {
    if (value === null || value === undefined || value === '') return { label: 'N/A', cls: 'status-na' };
    const v = Number(value);
    switch (param) {
      case 'pH':
        if (v >= 6.5 && v <= 8.5) return { label: 'Good', cls: 'status-good' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'DO (mg/L)':
        if (v >= 5.0) return { label: 'Good', cls: 'status-good' };
        if (v >= 3.0) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'BOD (mg/L)':
        if (v <= 3.0) return { label: 'Good', cls: 'status-good' };
        if (v <= 6.0) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'FC MPN/100ml':
      case 'TC MPN/100ml':
        if (v < 500) return { label: 'Good', cls: 'status-good' };
        if (v <= 1000) return { label: 'Moderate', cls: 'status-moderate' };
        return { label: 'Poor', cls: 'status-poor' };
      case 'Water Quality':
  // normalize and check for explicit 'non' first so "Non Complying" doesn't match 'comp'
  const _s = String(value).toLowerCase();
  if (_s.includes('non')) return { label: 'Non Complying', cls: 'status-poor' };
  if (_s.includes('comp')) return { label: 'Complying', cls: 'status-good' };
  return { label: String(value), cls: 'status-na' };
      default:
        return { label: '', cls: '' };
    }
  };

  return (
    <div className="page">
      <aside className="sidebar">
        <div className="brand">
          <FaWater size={28} color="#fff" />
          <div>
            <h1>RiverWatch</h1>
            <p>Water quality predictions for Pune rivers</p>
          </div>
        </div>
  <div className="legend">
          <div><span className="dot blue"/> Mula</div>
          <div><span className="dot teal"/> Mutha</div>
          <div><span className="dot orange"/> Mula-Mutha</div>
        </div>

        <div className="sidebar-info">
          <h3>Rivers</h3>
          <ul className="river-list">
            <li><span className="dot blue"/> <strong>Mula</strong>
              <div className="river-desc">Originates to the north — urban stretches through western Pune with monitoring points along bridges.</div>
            </li>
            <li><span className="dot teal"/> <strong>Mutha</strong>
              <div className="river-desc">Flows from Khadakvasla dam through Pune central areas; many monitoring points track water health downstream.</div>
            </li>
            <li><span className="dot orange"/> <strong>Mula-Mutha</strong>
              <div className="river-desc">Confluence section where Mula and Mutha meet — sensitive zone for mixing and quality changes.</div>
            </li>
          </ul>

          <h3 style={{marginTop:12}}>Parameters</h3>
          <ul className="param-names">
            <li>pH</li>
            <li>DO (mg/L)</li>
            <li>BOD (mg/L)</li>
            <li>FC MPN/100ml</li>
            <li>TC MPN/100ml</li>
            <li>Water Quality (Complying / Non Complying)</li>
          </ul>
        </div>
      </aside>

      <main className="main">
        <MapContainer center={[puneCenter.latitude, puneCenter.longitude]} zoom={puneCenter.zoom} className="map">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {puneLocations.map(loc => (
            <CircleMarker key={loc.id}
              center={[loc.coordinate.latitude, loc.coordinate.longitude]}
              pathOptions={{ color: getRiverColor(loc.river), fillColor: getRiverColor(loc.river), fillOpacity: 0.9 }}
              radius={10}
              eventHandlers={{ click: () => handleMarkerClick(loc) }}
            >
              <Popup>
                <div className="popup-card">
                  <div className="popup-header">
                    <strong className="popup-title">{loc.name}</strong>
                    <div className="muted popup-sub">{loc.river}</div>
                  </div>

                  {predictions[loc.id] ? (
                    <ul className="param-list popup-grid">
                      {Object.entries(predictions[loc.id]).map(([k, v]) => {
                        const status = getParameterStatus(k, v);
                        if (k === 'Water Quality') {
                          return (
                            <li className="param-row" key={k}>
                              <span className="param-name">{k}</span>
                              <span className="param-value"><span className={`badge ${status.cls}`}>{status.label}</span></span>
                            </li>
                          );
                        }

                        return (
                          <li className="param-row" key={k}>
                            <span className="param-name">{k}</span>
                            <span className="param-value"><span className="val-text">{v}</span><span className={`badge ${status.cls}`}>{status.label}</span></span>
                          </li>
                        );
                      })}
                    </ul>
                  ) : (
                    <div>Loading predictions...</div>
                  )}
                </div>
              </Popup>
            </CircleMarker>
          ))}
        </MapContainer>

  <div className="detail-panel" style={{ '--river-color': getRiverColor(selected ? selected.river : 'Mula') }}>
          <div className="detail-calendar">
            <Calendar date={selectedDate} onChange={d => setSelectedDate(d)} />
          </div>
          <div className="selected-date">Selected date: <strong>{new Date(selectedDate).toLocaleDateString()}</strong></div>
          {selected ? (
            <div>
              <h2>{selected.name}</h2>
              <p className="muted">{selected.river} • {selected.description}</p>
              <h3>Prediction for {new Date(selectedDate).toLocaleDateString()}</h3>
              {selected.prediction ? (
                <ul className="param-list large">
                  {Object.entries(selected.prediction).filter(([k]) => k !== 'Water Quality').map(([k, v]) => {
                    const status = getParameterStatus(k, v);
                    return <li key={k}><span className="param">{k}</span><span className="value">{v}</span><span className={`badge ${status.cls}`}>{status.label}</span></li>
                  })}
                  {(() => {
                    const q = selected.prediction['Water Quality'];
                    const status = getParameterStatus('Water Quality', q);
                    return (
                      <li className="quality">
                        <span className="param">Water Quality</span>
                        <span className="param-value"><span className={`badge ${status.cls}`}>{status.label}</span></span>
                      </li>
                    );
                  })()}
                </ul>
              ) : <div>Loading...</div>}
            </div>
          ) : (
            <div className="placeholder">Select a location on the map or from the list.</div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
